import React, { useEffect, useState } from "react";
import axios from "axios";
import styles from "./Sidebar.module.css";

const Sidebar = ({ onNewChat, user_id, onLoadSession }) => {
  const [chatSessions, setChatSessions] = useState([]);

  // Fetch chat sessions when the component mounts or when user_id changes
  useEffect(() => {
    fetchChatSessions();
  }, [user_id]);

  // Function to fetch chat sessions
  const fetchChatSessions = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/chat_sessions/${user_id}`);
      setChatSessions(response.data.sessions);
    } catch (error) {
      console.error("Error fetching chat sessions:", error);
    }
  };

  // Function to handle new chat creation and refresh the list
  const handleNewChat = async () => {
    await onNewChat(); // Call parent function to create a new chat
    fetchChatSessions(); // Refresh chat list immediately
  };

  // Function to truncate conversation titles
  const truncateTitle = (title, maxLength = 25) => {
    return title.length > maxLength ? title.substring(0, maxLength) + "..." : title;
  };

  return (
    <div className={styles.sidebar}>
      <div className={styles.sidebarHeader}>
        <h2>EVA</h2>
        <p>Hi, I'm Educational Virtual Assistant (EVA).</p>
      </div>
      <nav className={styles.sidebarNav}>
        {/* New Chat Button */}
        <button onClick={handleNewChat} className={styles.navButton}>
          + New Chat
        </button>

        {/* List of Chat Sessions */}
        <div className={styles.chatSessions}>
          {chatSessions.map((session) => (
            <button
              key={session.id}
              className={styles.sessionButton}
              onClick={() => onLoadSession(session.id)} // Load the selected session
              title={session.title} // Show full title on hover
            >
              {truncateTitle(session.title)}
            </button>
          ))}
        </div>
      </nav>
    </div>
  );
};

export default Sidebar;
